    public static List<Integer> readTable(byte[] data, int start, int end) {
        List<Integer> offsets = new ArrayList<>();
        int offset = start;
        while (offset<end) {
            int a = data[offset];
            int b = data[offset+1];
            int c = data[offset+2];
            c = c - x("C0");
            int offsetData = (c & 0xFF)*x("10000") + (b & 0xFF)*x("100")+(a & 0xFF);
            offsets.add(offsetData);
            offset += 3;
        }
        return offsets;
    }
    
